+  session: {
+    access_token: null,
+    refresh_token: null,
+    expires_at: null,
+    token_type: 'bearer' as const,
+    email_confirmed: false,
+  },
+  user: {
+    id: '22222222-2222-2222-2222-222222222222',
+    email: 'playwright@example.com',
+    email_confirmed_at: null,
+  },
