+import type { Preview } from '@storybook/react';
+import { useEffect } from 'react';
+import '../../apps/frontend/app/globals.css';
+import { ThemeProvider } from '../../apps/frontend/components/design-system/ThemeProvider';
+
+declare module '@storybook/react' {
+  interface GlobalTypes {
+    theme?: 'system' | 'light' | 'dark';
+  }
+}
+
+const withDocumentTheme = (theme: 'system' | 'light' | 'dark') => {
+  if (typeof document === 'undefined') {
+    return;
+  }
+
+  if (theme === 'system') {
+    document.documentElement.removeAttribute('data-theme');
+    return;
+  }
+
+  document.documentElement.setAttribute('data-theme', theme);
+};
+
+const preview: Preview = {
+  parameters: {
+    actions: { argTypesRegex: '^on[A-Z].*' },
+    controls: {
+      matchers: {
+        color: /(background|color)$/i,
+        date: /Date$/,
+      },
+    },
+    layout: 'fullscreen',
+    backgrounds: { disable: true },
+  },
+  globalTypes: {
+    theme: {
+      description: 'Theme preference applied via ThemeProvider',
+      defaultValue: 'system',
+      toolbar: {
+        icon: 'mirror',
+        dynamicTitle: true,
+        items: [
+          { value: 'system', left: '💻', title: 'System' },
+          { value: 'light', left: '☀️', title: 'Light' },
+          { value: 'dark', left: '🌙', title: 'Dark' },
+        ],
+      },
+    },
+  },
+  decorators: [
+    (Story, context) => {
+      const theme = (context.globals.theme as 'system' | 'light' | 'dark') ?? 'system';
+
+      useEffect(() => {
+        withDocumentTheme(theme);
+      }, [theme]);
+
+      return (
+        <ThemeProvider forcedTheme={theme}>
+          <Story />
+        </ThemeProvider>
+      );
+    },
+  ],
+};
+
+export default preview;
*** End Patch

