              {groupMetadata[category].description}
            </p>
          </header>
          <div className="grid gap-4 md:grid-cols-2">
            {groupTokens.map((token) => (
              <TokenCard key={token.id} token={token} category={category} />
            ))}
          </div>
        </div>
      ))}
    </section>
  );
}

export default TokenGallery;
